package main

import "github.com/lukechampine/advent/utils"

const input = `



`

type pos struct {
	x, y int
}

func key(keypad [][]string, p pos) string {
	if p.x >= len(keypad) || p.x < 0 || p.y < 0 || p.y >= len(keypad) {
		return " "
	}
	return keypad[p.y][p.x]
}

func move(p pos, dir rune) pos {
	switch dir {
	case 'U':
		p.y--
	case 'D':
		p.y++
	case 'R':
		p.x++
	case 'L':
		p.x--
	}
	return p
}

func follow(p pos, keypad [][]string, dirs []string) string {
	var keys string
	for _, line := range dirs {
		for _, d := range line {
			if newpos := move(p, d); key(keypad, newpos) != " " {
				p = newpos
			}
		}
		keys += key(keypad, p)
	}
	return keys
}

func main() {
	// part 1
	dirs := utils.Split(input, "\n")
	p := pos{1, 1}
	keypad := [][]string{
		{"1", "2", "3"},
		{"4", "5", "6"},
		{"7", "8", "9"},
	}
	utils.Println(follow(p, keypad, dirs))

	// part 2
	p = pos{0, 2}
	keypad = [][]string{
		{" ", " ", "1", " ", " "},
		{" ", "2", "3", "4", " "},
		{"5", "6", "7", "8", "9"},
		{" ", "A", "B", "C", " "},
		{" ", " ", "D", " ", " "},
	}
	utils.Println(follow(p, keypad, dirs))
}
