package main

import "github.com/lukechampine/advent/utils"

const input = `



`

func key(keypad [][]string, p utils.Pos) string {
	if p.X >= len(keypad) || p.X < 0 || p.Y < 0 || p.Y >= len(keypad) {
		return " "
	}
	return keypad[p.Y][p.X]
}

func move(p utils.Pos, dir rune) utils.Pos {
	switch dir {
	case 'U':
		p.Y--
	case 'D':
		p.Y++
	case 'R':
		p.X++
	case 'L':
		p.X--
	}
	return p
}

func follow(p utils.Pos, keypad [][]string, dirs []string) string {
	var keys string
	for _, line := range dirs {
		for _, d := range line {
			if newpos := move(p, d); key(keypad, newpos) != " " {
				p = newpos
			}
		}
		keys += key(keypad, p)
	}
	return keys
}

func main() {
	// part 1
	dirs := utils.Split(input, "\n")
	p := utils.Pos{1, 1}
	keypad := [][]string{
		{"1", "2", "3"},
		{"4", "5", "6"},
		{"7", "8", "9"},
	}
	utils.Println(follow(p, keypad, dirs))

	// part 2
	p = utils.Pos{0, 2}
	keypad = [][]string{
		{" ", " ", "1", " ", " "},
		{" ", "2", "3", "4", " "},
		{"5", "6", "7", "8", "9"},
		{" ", "A", "B", "C", " "},
		{" ", " ", "D", " ", " "},
	}
	utils.Println(follow(p, keypad, dirs))
}
