func decompress(str string) int {
	count := 0
	for i := 0; i < len(str); i++ {
		if str[i] == '(' {
			var l, n int
			utils.Sscanf(str[i:], "(%dx%d)", &l, &n)
			i += strings.Index(str[i:], ")") + l
			count += n * l
		} else {
			count++
		}
	}
	return count
}

func recursiveDecompress(str string) int {
	count := 0
	for i := 0; i < len(str); i++ {
		if str[i] == '(' {
			var l, n int
			utils.Sscanf(str[i:], "(%dx%d)", &l, &n)
			i += strings.Index(str[i:], ")") + l
			count += n * recursiveDecompress(str[i-l+1:i+1])
		} else {
			count++
		}
	}
	return count
}

func main() {
	// part 1
	println(decompress(input))

	// part 2
	println(recursiveDecompress(input))
}
