func main() {
	// part 1
	grid := utils.Lines(input)
	p := utils.Pos{strings.IndexByte(grid[1], '|'), 1}
	next := utils.Pos{0, 1}
	letters := ""
	steps := 0
	for {
		// follow next until we hit +
		for grid[p.Y][p.X] != '+' && grid[p.Y][p.X] != ' ' {
			p.X, p.Y = p.X+next.X, p.Y+next.Y
			steps++
			if 'A' <= grid[p.Y][p.X] && grid[p.Y][p.X] <= 'Z' {
				letters += string(grid[p.Y][p.X])
			}

		}
		if grid[p.Y][p.X] == ' ' {
			// end of maze
			break
		}

		// determine next direction
		prev := utils.Pos{p.X - next.X, p.Y - next.Y}
		for _, m := range p.Moves() {
			if grid[m.Y][m.X] != ' ' && m != prev {
				next = utils.Pos{m.X - p.X, m.Y - p.Y}
				p = m
				steps++
				break
			}
		}

	}
	utils.Println(letters)

	// part 2
	utils.Println(steps)
}
