part1 :: String -> Int
part1 str = (count '(' str) - (count ')' str) where
	count x xs = length . filter (x ==) $ xs

part2 :: String -> Int
part2 str = length . takeWhile ((/= -1) . pos) . inits $ str where
	pos xs = sum . map delta $ xs
	delta '(' = 1
	delta ')' = -1
	delta _   = 0

